export const AIButton: React.FC<AIButtonProps> = ({ 
  text = 'GPT-Omni',
  variant = 'openai',
  className, 
  children,
  onClick,
  ...props 
}) => {
  const getIcon = () => {
    switch (variant) {
      case 'openai':
        return <OpenAIIcon />;
      case 'claude':
        return <ClaudeIcon />;
      case 'gemini':
        return <GeminiIcon />;
      case 'deepseek':
        return <DeepSeekIcon />;
      default:
        return <OpenAIIcon />;
    }
  };

  const getDefaultText = () => {
    switch (variant) {
      case 'gemini':
        return 'Google Gemini';
      case 'deepseek':
        return 'DeepSeek';
      case 'claude':
        return 'Claude 3.5';
      default:
        return 'GPT-Omni';
    }
  };

  return (
    <StyledAIButton 
      onClick={onClick} 
      className={className} 
      variant={variant}
      {...props}
    >
      <Logo variant={variant}>
        {getIcon()}
      </Logo>
      <ButtonText variant={variant}>
        <span>Powered By</span>
        <span>{text || children || getDefaultText()}</span>
      </ButtonText>
    </StyledAIButton>
  );
}; 